mapper.select:
select no, content, senderId, senderName, senderPhoto, sendDate
from (
	select rownum rnum, no, content, senderId, senderName, senderPhoto, sendDate
	from (
		select
			m.no, m.content, m.senderId, sm.name senderName, sm.photo senderPhoto, m.sendDate
		from
			message m, member sm
		where 
			(
				accepterId = #{accepter}
				and acceptDate is null
			) and (
				m.senderId = sm.id
			)
		order by m.no desc
	)
)
where rnum between #{startRow} and #{endRow}

select no, senderId, senderName, sendDate, senderPhoto, accepterId, accepterName, acceptDate, accepterPhoto from ( select rownum rnum, no, senderId, senderName, sendDate, senderPhoto, accepterId, accepterName, acceptDate, accepterPhoto from ( select m.no, m.senderId, sm.name senderName, m.sendDate, sm.photo senderPhoto, m.accepterId, am.name accepterName, m.acceptDate, am.photo accepterPhoto from message m, member sm, member am where
and (m.senderId = sm.id and m.accepterId = am.id)
order by no desc ) ) where rnum between #{startRow} and #{endRow}

